// Distance between relation lines
var distanceBetweenLines = 4;

// Maximal allowed name length displayed in graph
var maxNameLength = 20;

// Value is initialized in document.ready and references bird eye canvas
var birdEyeBackCanvas = null;

// Indicates whether bird eye refresh is invoked
var isBirdEyeRefreshInvoked;

// Stores previous height of a window
var previousHeight = 0;

