function init() {

	var levelDistanceIndex = 1;
	if (graph_data.children.length < 10) {
		levelDistanceIndex = 0.75;
	} else if (graph_data.children.length > 30 && $(window).height() < 1400) {
		levelDistanceIndex = 1.33;
	}

	$jit.RGraph.Plot.NodeTypes.implement({
		'icon':
		{
			'render': function (node, canvas) {
				var ctx = canvas.getCtx();
				var pos = node.getPos().toComplex();

				var item = rootid == node.id
					? graph_data
					: getSubItem(graph_data.children, node.id);

				var drawnImage = null;
				var moveOffsetX = computeNodeOffsets(rgraph, node);
				var moveOffsetY = computeNodeOffsets(rgraph, node);

				if (node.data.type == "company") {
					drawnImage = rgraph.graph.getNode(rgraph.root).id == node.id
						? companyBigImg
						: (item.data.isExpandable == false ? companyImg : companyPlusImg);
				} else {
					drawnImage = rgraph.graph.getNode(rgraph.root).id == node.id
						? personBigImg
						: (item.data.isExpandable == false ? personImg : personPlusImg);
				}

				// draw to real canvas
				var drawX = pos.x - moveOffsetX;
				var drawY = pos.y - moveOffsetY;
				ctx.drawImage(drawnImage, drawX, drawY);

				// invoke bird eye refresh
				isBirdEyeRefreshInvoked = true;
			},
			'contains': function (node, pos) {
				var npos = node.pos.getc(true);
				var moveOffset = rgraph.graph.getNode(rgraph.root).id == node.id
					? 30
					: 16;

				return this.nodeHelper.square.contains(npos, pos, moveOffset);
			}
		},
	});

	var containsMidArrow = function (posFrom, posTo, pos, epsilon) {
		return $jit.EdgeHelper.line.contains(posFrom, posTo, pos, epsilon);
	};

	var getSubItem = function (subItems, id) {
		if (subItems) {
			for (var i = 0; i < subItems.length; i++) {
				if (subItems[i].id == id) {
					return subItems[i];
				}
				var found = getSubItem(subItems[i].children, id);
				if (found) return found;
			}
		}
	};

	var findAdjacencies = function (nodeFromId, nodeToId, getDefault) {
		var adjacenciesColors = [];

		var item = rootid == nodeToId
			? graph_data
			: getSubItem(graph_data.children, nodeToId);

		if (item.children != undefined) {
			for (i = 0; i < item.children.length; i++) {
				if (item.children[i]['id'] == nodeFromId) {
					adjacenciesColors.push(item.children[i].data.relationColor);
				}
			}
		} else if (getDefault == true) {
			adjacenciesColors.push(item.data.relationColor);
		}

		return adjacenciesColors;
	};

	$jit.RGraph.Plot.EdgeTypes.implement({
		'midArrow': {
			'render': function (adj, canvas) {
				// retrieve adjacency colors, try that edge starts from root node, if not try other direction
				var adjacenciesColors = findAdjacencies(adj.nodeFrom.id, adj.nodeTo.id, false);

				if (adjacenciesColors.length == 0) {
					adjacenciesColors = findAdjacencies(adj.nodeTo.id, adj.nodeFrom.id, true);
				}

				// render all adjancencies
				var i;
				for (i = 0; i < adjacenciesColors.length; i++) {
					var from = adj.nodeFrom.pos.getc(true);
					var to = adj.nodeTo.pos.getc(true);
					var differenceX = Math.abs(from.x - to.x);
					var differenceY = Math.abs(from.y - to.y);
					var centeredOffset = Math.round(-2 + ((adjacenciesColors.length / 2) * distanceBetweenLines) - (i * distanceBetweenLines)) + 0.5;

					if (Math.abs(Math.floor(differenceY) / Math.floor(((differenceX == 0) ? 1 : differenceX))) < 1) {
						from.y = from.y - centeredOffset + distanceBetweenLines;
						to.y = to.y - centeredOffset + distanceBetweenLines;
					} else {
						from.x = from.x - centeredOffset;
						to.x = to.x - centeredOffset;
					}

					renderLine(from.x, from.y, to.x, to.y, canvas.getCtx(), '#' + adjacenciesColors[i]);
				}
			},

			'contains': function (adj, pos) {
				var from = adj.nodeFrom.pos.getc(true),
					to = adj.nodeTo.pos.getc(true);
				return containsMidArrow(from, to, pos, this.edge.epsilon);
			}
		}
	});

	function nodeClick(node) {
		// prevent another load when previous node is being loaded
		if (isLoadingNode == true) {
			return;
		}

		isLoadingNode = true;

		if (sessionStorage.graphClickType == 'newpage') {
			var referencedate = $("[name=referencedate]").val();
			referencedate = referencedate.replace(".", "").replace(".", "");
			window.location = baseurl + "vazby/" + node.id + "/i/" + referencedate;
		} else if (sessionStorage.graphClickType == 'opennode') {
			var isClicked = false;
			for (var index in clickedNodes) {
				if (clickedNodes[index] == node.id) {
					isClicked = true;
					break;
				}
			}

			var item = getSubItem(graph_data.children, node.id);

			if (item == undefined) {
				item = rgraph.graph.getNode(rgraph.root);
			}

			// center graph
			var x = -rgraph.canvas.translateOffsetX;
			var y = -rgraph.canvas.translateOffsetY;
			rgraph.canvas.translate(x, y);

			if (item.data.isExpandable == false || isClicked == true) {
				// center node and refresh legend
				rgraph.onClick(node.id, null);

				// recalculate heights as content is different
				isLoadingNode = false;
			} else {
				// navigate to another page
				$.post(baseurl + "relations/getchildren/" + node.id, makeChildrenPostData($("[name=referencedate]").val()), function (data, textStatus) {
					// TODO: handle text status
					// do not update info for objects that are already contained in the graph
					var i = 0;
					for (i = 0; i < data.children.length; i++) {
						var found = false;

						rgraph.graph.eachNode(function (node) {
							if (found == false && node.id == data.children[i].id) {
								data.children[i].data.details = node.data.details;
								found = true;
							}
						});
					}
					item.children = data.children;
					item.data.isExpandable = false;
					item.data.details = data.data.details;

					rgraph.loadJSON(graph_data);
					rgraph.refresh();
					rgraph.onClick(node.id, null);

					clickedNodes.push(node.id);

					loadchangedates();
					isLoadingNode = false;
				}, "json");
			}
		}
	}

	rgraph = new $jit.RGraph({
		injectInto: 'infovis',
		background: {
			CanvasStyles: {
				strokeStyle: '#BFCCD4',
				fillStyle: '#FFFFFF'
			}
		},
		duration: 500,
		levelsToShow: 1,
		levelDistance: (($(window).height() - 150) / 2) * levelDistanceIndex,
		numberOfCircles: 8, // satisfies vertically
		Navigation: {
			enable: true,
			panning: 'avoid nodes'
		},

		Node: {
			overridable: true,
			type: 'icon'
		},

		Edge: {
			overridable: true,
			type: 'midArrow',
		},

		onBeforeCompute: function (node) {
			var item = getSubItem(graph_data.children, node.id);
			if (item == undefined) {
				item = rgraph.graph.getNode(rgraph.root);
			}

			$jit.id('subject_data').innerHTML = item.data.details;
		},

		onCreateLabel: function (domElement, node) {
			// shorten label
			var name = htmlDecode(node.name);
			if (name.length > maxNameLength) {
				domElement.title = name;
				name = name.substr(0, maxNameLength) + "...";
			}

			domElement.innerHTML = htmlEncode(name);

			if (node.name != hidden_node_name) {
				domElement.onclick = function () {
					nodeClick(node);
				};
			}
		},

		onPlaceLabel: function (domElement, node) {
			var style = domElement.style;
			style.display = '';
			style.cursor = 'pointer';

			var top = parseInt(style.top);

			var currentZoomIndexHigh = currentZoom;
			var currentZoomIndexLow = currentZoom;

			if (currentZoom < 0.8 && currentZoom >= 0.7) {
				currentZoomIndexHigh = currentZoom * 0.8;
				currentZoomIndexLow = currentZoom * 0.5;
			} else if (currentZoom < 0.7 && currentZoom >= 0.6) {
				currentZoomIndexHigh = currentZoom * 0.7;
				currentZoomIndexLow = currentZoom * 0.4;
			} else if (currentZoom < 0.6) {
				currentZoomIndexHigh = currentZoom * 0.6;
				currentZoomIndexLow = currentZoom * 0.2;

				style.display = 'none';
			}

			if (node.id == rgraph.root) {
				style.fontSize = (rootLabelFontSize * rgraph.canvas.scaleOffsetX) + "px";
				style.fontWeight = 'bold';
				top = top + (32 * currentZoomIndexHigh);
			} else {
				style.fontSize = (labelFontSize * rgraph.canvas.scaleOffsetX) + "px";
				style.fontWeight = 'normal';
				top = top + (12 * currentZoomIndexLow);
			}

			style.top = (top + (11 * rgraph.canvas.scaleOffsetX)) + 'px';

			// same style as .blurred class
			if (node.name == hidden_node_name) {
				domElement.className = domElement.className + " blurred";
			}

			var left = parseInt(style.left);
			var w = domElement.offsetWidth;
			style.left = (left - w / 2) + 'px';
		},

		Events: {
			enable: true,
			type: 'Native',

			onMouseEnter: function (node, eventInfo, e) {
				rgraph.canvas.getElement().style.cursor = 'pointer';
			},

			onMouseLeave: function (node, eventInfo, e) {
				rgraph.canvas.getElement().style.cursor = '';
			},

			onDragMove: function (node, eventInfo, e) {
				var pos = eventInfo.getPos();
				node.pos.setc(pos.x, pos.y);
				rgraph.plot();
			},

			onClick: function (node, eventInfo, e) {
				if (!node || node.name == hidden_node_name) return;
				nodeClick(node);
			}
		}
	});

	//load JSON data
	rgraph.loadJSON(graph_data);

	rgraph.compute('end');
	rgraph.fx.animate({
		modes: ['polar'],
		duration: 200
	});

	$jit.id('subject_data').innerHTML = rgraph.graph.getNode(rgraph.root).data.details;

	clickedNodes.push(rgraph.root);
}

function renderLine(fromx, fromy, tox, toy, canvas, color) {
	canvas.beginPath();
	canvas.moveTo(fromx, fromy);
	canvas.lineTo(tox, toy);
	canvas.strokeStyle = color;
	canvas.stroke();
}

function graphclicktype_visualize() {
	var newText = sessionStorage.graphClickType == 'newpage'
		? 'Zobrazí se jen vazby posledně zadaného subjektu'
		: 'Zobrazují se původně vyhledané i další zadané vazby';

	if (sessionStorage.graphClickType == 'newpage') {
		$("#graphstructure_checkbox").removeAttr("checked");
	}

	$("#graphstructure_tooltip").attr('title', newText);
}

function relation_types_visualise() {
	if (sessionStorage.showrelationcolors && sessionStorage.showrelationcolors != null) {
		var showRelationColors = JSON.parse(sessionStorage.showrelationcolors);
		var parsedShowRelationColors = [];
		for (var i = 0; i < showRelationColors.length; i++) {
			parsedShowRelationColors[showRelationColors[i]] = true;
		}

		$(".relationtype_checkbox").each(function() {
			if (parsedShowRelationColors != null && !parsedShowRelationColors[$(this).attr("data-color")]) {
				$(this).removeAttr("checked");
			}
		});
	}
}

function initializeArrowNavigation(clicker, x, y) {
	var timeout;

	var handleMouseDown = function() {
		timeout = setInterval(function () {
			rgraph.canvas.translate(x, y);
			return false;
		}, 100);

		return false;
	}

	clicker.mousedown(function (e) {
		return handleMouseDown();
	});

	clicker.on({ 'touchstart' : function() {
		return handleMouseDown();
	}});

	clicker.click(function(e) {
		e.preventDefault();
	});

	$(document).mouseup(function () {
		clearInterval(timeout);
		return false;
	});

	$(document).on({ 'touchend' : function() {
		clearInterval(timeout);
		return false;
	}});
}

function htmlEncode(value) {
	//create a in-memory div, set it's inner text(which jQuery automatically encodes)
	//then grab the encoded contents back out.  The div never exists on the page.
	return $('<div/>').text(value).html();
}

function htmlDecode(value) {
	return $('<div/>').html(value).text();
}

function initBirdEyeBackground() {
	birdEyeBackCanvas = document.getElementById("birdeyeback").getContext("2d");
	var height = $("#birdeyeback").height() / birdeyeMultiplier;
	var width = $("#birdeyeback").width() / birdeyeMultiplier;

	$("#birdeye").height(height);
	$("#birdeye").css("top", -height * (birdeyeMultiplier - 0.7));
	$("#birdeye").css("left", width);

	if (!$("#birdeye").data('draggable')) {
		// this is the first birdeye init - initialize dragging
		$("#birdeye").draggable({
			containment: "parent",
			revert: true,
			stop: function (event, ui) {
				// get ratio between birdeye and real canvas

				var bindexX = ((previousWidth * birdeyeMultiplier) / $("#birdeye").width()) / birdeyeMultiplier;
				var bindexY = ((previousHeight * birdeyeMultiplier) / $("#birdeye").width()) / birdeyeMultiplier;

				rgraph.canvas.translate(
					(ui.originalPosition.left - ui.position.left) * bindexX,
					(ui.originalPosition.top - ui.position.top) * bindexY);
			}
		});
	}
}

function refreshBirdEyeBackground() {
	if (isBirdEyeRefreshInvoked) {

		var bindexX = (previousWidth * birdeyeMultiplier) / birdEyeBackCanvas.canvas.width;
		var bindexY = (previousHeight * (birdeyeMultiplier - 0.5)) / birdEyeBackCanvas.canvas.height;

		// clear current
		birdEyeBackCanvas.clearRect(0, 0, ($("#birdeyeback").width() * 2) + 9, $("#birdeyeback").height() * 2);

		// draw icons
		rgraph.graph.eachNode(function (node) {

			var pos = node.getPos().toComplex();

			var drawnImage = null;
			var moveOffsetX = computeNodeOffsets(rgraph, node);
			var moveOffsetY = computeNodeOffsets(rgraph, node);
			if (node.data.type == "company") {
				drawnImage = rgraph.graph.getNode(rgraph.root).id == node.id
					? smallcompanyBigImg
					: smallcompanyImg;
			} else {
				drawnImage = rgraph.graph.getNode(rgraph.root).id == node.id
					? smallpersonBigImg
					: smallpersonImg;
			}

			var smallDrawX = computeBirdEyeDrawTo(pos.x, moveOffsetX, bindexX, birdEyeBackCanvas.canvas.width, rgraph.canvas.translateOffsetX);
			var smallDrawY = computeBirdEyeDrawTo(pos.y, moveOffsetY, bindexY, birdEyeBackCanvas.canvas.height, rgraph.canvas.translateOffsetY);

			birdEyeBackCanvas.drawImage(drawnImage, smallDrawX, smallDrawY - 2);

			// draw lines
			node.eachAdjacency(function (adj) {
				var to = adj.nodeTo.getPos().toComplex();

				var smallDrawToX = computeBirdEyeDrawTo(to.x, moveOffsetX, bindexX, birdEyeBackCanvas.canvas.width, rgraph.canvas.translateOffsetX);
				var smallDrawToY = computeBirdEyeDrawTo(to.y, moveOffsetY, bindexY, birdEyeBackCanvas.canvas.height, rgraph.canvas.translateOffsetY);

				renderLine(smallDrawX + 2, smallDrawY + 2, smallDrawToX + 2, smallDrawToY + 2, birdEyeBackCanvas, 'rgba(0,0,0,0.2)');
			});

		});

		isBirdEyeRefreshInvoked = false;
	}
}

function computeNodeOffsets(rgraph, node) {
	return rgraph.graph.getNode(rgraph.root).id == node.id ? 45 : 24;
}

function computeBirdEyeDrawTo(initial, moveOffset, bindex, canvassize, translateOffset) {
	return ((initial - moveOffset) / bindex) + (canvassize / 2) + (translateOffset / bindex);
}

function recalculateBirdEyeHeight() {
	var width = ($("#infovis").width()) * birdeyeMultiplier;
	var height = ($("#infovis").height()) * birdeyeMultiplier;

	var heightToSet = height / (width / $("#birdeyeback").width());

	$("#birdeyeback").height(heightToSet);
	$("#navigation .content").height(heightToSet);
}

function resizeBirdeye() {
	recalculateBirdEyeHeight();
	initBirdEyeBackground();
	isBirdEyeRefreshInvoked = true;
}

function drawLabels(fillStyle) {
	rgraph.graph.eachNode(function (node) {
		var ctx = rgraph.canvas.getCtx();
		var coord = node.pos.getc(true);
		var yOffset = 0;

		if (node.id == rgraph.root) {
			ctx.font = "bold " + rootLabelFontSize;
			yOffset = 34;
		} else {
			ctx.font = labelFontSize;
			yOffset = 22;
		}

		ctx.fillStyle = fillStyle;
		ctx.textAlign = "center";

		var name = htmlDecode(node.name);
		if (name.length > maxNameLength) {
			name = name.substr(0, maxNameLength) + "...";
		}

		ctx.fillText(name, coord.x, coord.y + yOffset);
	});
}

function getShowRelationColors() {
	var showrelationcolors = [];
	$(".relationtype_checkbox:checked").each(function() {
		showrelationcolors.push($(this).attr('data-color'));
	});

	return showrelationcolors;
}

function makeChildrenPostData(dateText) {
	return {
		referencedate: dateText,
		showrelationcolors: getShowRelationColors()
	};
}

function loadrelations(dateText) {
	$.post(baseurl + "relations/getchildren/" + rgraph.root, makeChildrenPostData(dateText), function (data, textStatus) {
		// TODO: handle text status
		// remove old nodes and labels
		rgraph.graph.empty();
		rgraph.labels.clearLabels(true);
		rgraph.canvas.clear();
		clickedNodes = [];

		// set new nodes and labels
		graph_data = data;
		rgraph.loadJSON(data);
		rgraph.compute();
		rootid = rgraph.root;

		// select root node
		rgraph.onClick(rgraph.root);
		rgraph.canvas.translate(0, 0);

		// display details and mark node as clicked
		$jit.id('subject_data').innerHTML = rgraph.graph.getNode(rgraph.root).data.details;
		clickedNodes.push(rgraph.root);

	}, "json");
}

function setreferencedate(newDate) {
	$("[name=referencedate]").val(newDate);
	loadrelations(newDate);
}

function parseDate(input) {
	var parts = input.match(/(\d+)/g);
	// note parts[1]-1
	return new Date(parts[2], parts[1] - 1, parts[0]);
}

function change_timeline_arrow(id, disable) {
	$("#" + id).css('opacity', disable ? 0.5 : 1);
	$("#" + id).css('cursor', disable ? "default" : "pointer");
}

function loadchangedates() {
	change_timeline_arrow("timeline_back", true);
	change_timeline_arrow("timeline_forward", true);
	$.post(baseurl + "relations/getchangedates/" + rgraph.root, null, function (data, textStatus) {
		change_dates = data;
		if (data.length > 0) {
			// clear existing dates first
			$("#timeline_line table tr").empty();

			var refdate = parseDate($("[name=referencedate]").val());

			for (x in change_dates) {
				var parsedDate = parseDate(change_dates[x]);
				if (parsedDate < refdate) {
					change_timeline_arrow("timeline_back", false);
					break;
				}
			}

			for (x in change_dates) {
				var parsedDate = parseDate(change_dates[x]);
				if (parsedDate > refdate) {
					change_timeline_arrow("timeline_forward", false);
					break;
				}
			}

			for (x in change_dates) {
                var dateAndYearSplit = change_dates[x].replace(/\.([^.]+)$/, ':$1').split(':'); // .split('/\.(?=[^\.]+$)/');
                var topLinkValue = dateAndYearSplit[0].replace('.', ' / ');
                var bottomLinkValue = dateAndYearSplit[1];

				var addedTopLink = '<a href="' + change_dates[x] + '" style="position: relative; top: -16px;">';
				addedTopLink += topLinkValue;
				addedTopLink += '</a>';

                var addedDot = '<a href="' + change_dates[x] + '">';
				addedDot += '<img src="images/relations_timeline_dot.png" alt="" style="position: relative; top: -18px;" />';
				addedDot += '</a>';

				var addedBottomLink = '<a href="' + change_dates[x] + '" style="position: relative; top: -20px;">';
				addedBottomLink += bottomLinkValue;
				addedBottomLink += '</a>';

                $("#timeline_line table tr").append('<td>' + addedTopLink + addedDot + addedBottomLink + '</td>');
			}

			// scroll timeline div to right (there are latest changes)
			$("#timeline_line").scrollLeft($("#timeline_line table").width());

			// keep table and time line widths in sync (line appears for all points)
			$("#timeline_line div").width($("#timeline_line table").width());
		}
	}, "json");
}

$(document).ready(function () {
	// initialize type of behaviour graph does when user clicks a node
	if (sessionStorage.graphClickType == null) {
		sessionStorage.graphClickType = 'opennode'; // other value is newpage
	}

	previousHeight = $(window).height();
	previousWidth = $(window).width();

	// prepare canvas height and width
	$("#infovis").height($(window).height() - headerHeight);

	// use document instead of window here, because min-width is specified for body
	$("#infovis").width($("#subject_data").width());

	recalculateBirdEyeHeight();

	initBirdEyeBackground();
	setInterval(refreshBirdEyeBackground, 100);

	init(graph_data);

	graphclicktype_visualize();
	relation_types_visualise();

	// load relation after visualisation to take proper filters into account
	loadrelations($("[name=referencedate]").val());

	$(window).resize(function (e, f) {
		// center node
		var newWidth = $(window).width();
		var newHeight = $(window).height();

		headerHeight = calculateHeaderHeight();

		// resize canvas
		$("#infovis").height($(window).height() - headerHeight);

		// use document instead of window here, because min-width is specified for body
		$("#infovis").width($("#subject_data").width());

		rgraph.canvas.resize($('#infovis').width(), $('#infovis').height());

		// store new values
		previousHeight = newHeight;
		previousWidth = newWidth;

		// affect resizing to birdeye and legend (bird eye has to go first as it affects computed elements)
		resizeBirdeye();

		handleResize();
	});

	$(document).on("click", "#subject_data ul > li", function () {
		var targetNode = $(this).attr("id").replace("target_", "");
		$("div[id*='" + targetNode + "']").click();
		return false;
	});

	$("#graphstructure_checkbox").change(function () {
		// toggle value
		sessionStorage.graphClickType = (sessionStorage.graphClickType == 'opennode') ? 'newpage' : 'opennode';

		graphclicktype_visualize();
		return false;
	});

	$(".relationtype_checkbox").change(function() {
		// save relations
		sessionStorage.showrelationcolors = JSON.stringify(getShowRelationColors());

		loadrelations($("[name=referencedate]").val());
	
		return false;
	});

	$("#zoom_in").click(function() {
		if (currentZoom < 2) {
			currentZoom = currentZoom + 0.1;
			rgraph.canvas.setZoom(currentZoom, currentZoom);
		}

		return false;
	});

	$("#zoom_out").click(function() {
		if (currentZoom > 0.3) {
			currentZoom = currentZoom - 0.1;
			rgraph.canvas.setZoom(currentZoom, currentZoom);
		}

		return false;
	});

	initializeArrowNavigation($("#direction_left"), -10, 0);
	initializeArrowNavigation($("#direction_right"), 10, 0);
	initializeArrowNavigation($("#direction_top"), 0, -10);
	initializeArrowNavigation($("#direction_bottom"), 0, 10);

	(function ($) {
		$.datepicker.regional['cs'] = {
			renderer: $.ui.datepicker.defaultRenderer,
			monthNames: ['leden', 'únor', 'březen', 'duben', 'květen', 'červen', 'červenec', 'srpen', 'září', 'říjen', 'listopad', 'prosinec'],
			monthNamesShort: ['led', 'úno', 'bře', 'dub', 'kvě', 'čer', 'čvc', 'srp', 'zář', 'říj', 'lis', 'pro'],
			dayNames: ['neděle', 'pondělí', 'úterý', 'středa', 'čtvrtek', 'pátek', 'sobota'],
			dayNamesShort: ['ne', 'po', 'út', 'st', 'čt', 'pá', 'so'],
			dayNamesMin: ['ne', 'po', 'út', 'st', 'čt', 'pá', 'so'],
			dateFormat: 'dd.mm.yy',
			firstDay: 1,
			prevText: '&#x3c;Dříve', prevStatus: '',
			prevJumpText: '&#x3c;&#x3c;', prevJumpStatus: '',
			nextText: 'Později&#x3e;', nextStatus: '',
			nextJumpText: '&#x3e;&#x3e;', nextJumpStatus: '',
			currentText: 'Dnes', currentStatus: '',
			todayText: 'Dnes', todayStatus: '',
			clearText: '-', clearStatus: '',
			closeText: 'Zavřít', closeStatus: '',
			yearStatus: '', monthStatus: '',
			weekText: 'Týd', weekStatus: '',
			dayStatus: 'DD d MM',
			defaultStatus: '',
			isRTL: false
		};
		$.extend($.datepicker.defaults, $.datepicker.regional['cs']);
	})(jQuery);

	$.datepicker.setDefaults($.datepicker.regional["cs"]);

	$("[name=referencedate]").datepicker({
		changeMonth: true,
		changeYear: true,
		showButtonPanel: true,
		showOn: "both",
		buttonImage: "images/calendar_relations.png",
		buttonImageOnly: true,
		yearRange: "-110:+0",
		onSelect: function (dateText) {
			loadrelations(dateText);
		}
	});

	// load list of dates when changes to relations were made
	loadchangedates();

	$("#timeline_back").click(function () {
		var refdate = parseDate($("[name=referencedate]").val());
		var disable = true;
		for (var i = change_dates.length - 1; i >= 0; i--) {
			var parsedDate = parseDate(change_dates[i]);
			if (parsedDate < refdate) {
				setreferencedate(change_dates[i]);
				disable = i == 0;

				if (change_dates.length > 1) {
					change_timeline_arrow("timeline_forward", false);
				}

				break;
			}
		}

		if (disable == true) {
			change_timeline_arrow("timeline_back", true);
		}

		return false;
	});

	$(document).on("click", "#timeline_line a", function () {
		var date = $(this).attr("href");
		setreferencedate(date);

		if (change_dates.length > 0) {
			change_timeline_arrow("timeline_back", date == change_dates[0]);
			change_timeline_arrow("timeline_forward", date == change_dates[change_dates.length - 1]);
		}

		return false;
	});

	$("#timeline_forward").click(function () {
		var refdate = parseDate($("[name=referencedate]").val());
		var disable = true;
		for (var i = 0; i < change_dates.length; i++) {
			var parsedDate = parseDate(change_dates[i]);
			if (parsedDate > refdate) {
				setreferencedate(change_dates[i]);
				disable = i == change_dates.length - 1;

				if (change_dates.length > 1) {
					change_timeline_arrow("timeline_back", false);
				}

				break;
			}
		}

		if (disable == true) {
			change_timeline_arrow("timeline_forward", true);
		}

		return false;
	});

	function handleResize() {
		// keep table and time line widths in sync (line appears for all points)
		$("#timeline_line div").width($("#timeline_line table").width());
	}

	// set positons at the load
	handleResize();
});